import React, { useEffect, useRef } from "react";
import { AnimatePresence, motion } from "framer-motion";
import gsap from "gsap";
import "./mainPage.css";

const media = [
  {
    type: "",
    src: "",
  },
  {
    type: "image",
    src: "https://cdn.mos.cms.futurecdn.net/oLMgm6qmfPahnSjaSdn9Da.jpg",
  },
];

function MainPage() {
  // Create refs for sections
  const aboutRef = useRef(null);
  const contactRef = useRef(null);
  const socialMediaRef = useRef(null);

  useEffect(() => {
    const screenWidth = window.innerWidth;
    const startX = screenWidth / 4;
    const startyX = -screenWidth / 4;

    gsap.fromTo(
      ".textOne",
      { x: startyX },
      { duration: 1, x: 0, ease: "slow" }
    );
    gsap.fromTo(
      ".textTwo",
      { x: startyX },
      { duration: 1, x: 0, ease: "slow" }
    );

    gsap.fromTo(
      ".textThree",
      { x: startX },
      { duration: 1, x: 0, ease: "slow" }
    );
  }, []);

  // Function to handle smooth scrolling
  const scrollToSection = (ref) => {
    window.scrollTo({
      top: ref.current.offsetTop,
      behavior: "smooth",
    });
  };

  return (
    <AnimatePresence>
      <div className="imageContainer">
        <div className="textContainer">
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 2 }}
            className="textOne"
          >
            Aurora
          </motion.div>

          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1.5 }}
            className="textThree"
          >
            Studio
          </motion.div>
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1.5 }}
            className="textTwo"
          >
            Ink
          </motion.div>
        </div>
        <div className="listContainer">
          <motion.button
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.5, duration: 1.5 }}
            className="list"
            onClick={() => scrollToSection(aboutRef)}
          >
            About
          </motion.button>
          <motion.button
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 1, duration: 1.5 }}
            className="list"
            onClick={() => scrollToSection(contactRef)}
          >
            Contact
          </motion.button>
          <motion.button
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 1.5, duration: 1.5 }}
            className="list"
            onClick={() => scrollToSection(socialMediaRef)}
          >
            Social Media
          </motion.button>
        </div>
      </div>
      {/* About Section */}
      <div ref={aboutRef} className="section aboutSection">
        <h2>About Us</h2>
        <p>This is the About section content.</p>
      </div>
      {/* Contact Section */}
      <div ref={contactRef} className="section contactSection">
        <h2>Contact Us</h2>
        <p>This is the Contact section content.</p>
      </div>
      {/* Social Media Section */}
      <div ref={socialMediaRef} className="section socialMediaSection">
        <h2>Follow Us on Social Media</h2>
        <p>This is the Social Media section content.</p>
      </div>
    </AnimatePresence>
  );
}

export default MainPage;
