import React, { useEffect } from "react";
import { AnimatePresence, motion } from "framer-motion";
import gsap from "gsap";
import "./mainPage.css";

const media = [
  {
    type: "",
    src: "",
  },
  {
    type: "image",
    src: "https://cdn.mos.cms.futurecdn.net/oLMgm6qmfPahnSjaSdn9Da.jpg",
  },
];

function MainPage() {
  useEffect(() => {
    const screenWidth = window.innerWidth;
    const startX = screenWidth / 4;
    const startyX = -screenWidth / 4;

    gsap.fromTo(
      ".textOne",
      { x: startyX },
      { duration: 1, x: 0, ease: "slow" }
    );
    gsap.fromTo(
      ".textTwo",
      { x: startyX },
      { duration: 1, x: 0, ease: "slow" }
    );

    gsap.fromTo(
      ".textThree",
      { x: startX },
      { duration: 1, x: 0, ease: "slow" }
    );
  }, []);

  return (
    <AnimatePresence>
      <div className="imageContainer">
        <div className="textContainer">
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 2 }}
            className="textOne"
          >
            Aurora
          </motion.div>

          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1.5 }}
            className="textThree"
          >
            Studio
          </motion.div>
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1.5 }}
            className="textTwo"
          >
            Ink
          </motion.div>
        </div>
        <div className="listContainer">
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 2, duration: 1.5 }}
            className="list"
          >
            About
          </motion.div>
        </div>

