import "./mainPage.css";
import { AnimatePresence, motion } from "framer-motion";
import gsap from "gsap";
import React from "react";
import { useEffect } from "react";

const media = [
  {
    type: "",
    src: "",
  },
  {
    type: "image",
    src: "https://cdn.mos.cms.futurecdn.net/oLMgm6qmfPahnSjaSdn9Da.jpg",
  },
];

function MainPage() {
  useEffect(() => {
    gsap.fromTo(
      ".circle",
      { y: -200 },
      { delay: 1, y: 0, duration: 1.5, ease: "power1.inOut" }
    );
  }, []);

  useEffect(() => {
    const screenWidth = window.innerWidth;
    const startX = screenWidth / 4;
    const startyX = -screenWidth / 4;

    gsap.fromTo(
      ".textOne",
      { x: startyX },
      { duration: 2.5, x: 0, ease: "slow" }
    );

    gsap.fromTo(
      ".textTwo",
      { x: startX },
      { duration: 2.5, x: 0, ease: "slow" }
    );
  }, []);

  return (
    <AnimatePresence>
      <div className="imageContainer">
        <div className="textContainer">
          <motion.text
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 2, duration: 2 }}
            className="textOne"
          >
            Aurora Ink
          </motion.text>
          <motion.text
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 2, duration: 2 }}
            className="textTwo"
          >
            Studio
          </motion.text>
        </div>
        <motion.text
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 2, duration: 2 }}
          className="list"
        >
          Studio
        </motion.text>

        {media.map((item, index) => {
          if (item.type === "image") {
            return (
              <img
                key={index}
                alt="media"
                src={item.src}
                width="100%"
                height="100%"
              />
            );
          } else if (item.type === "video") {
            return (
              <video
                key={index}
                width="100%"
                height="100%"
                autoPlay={true}
                loop={true}
                muted
              >
                <source src={item.src} type="video/mp4" />
                Your browser does not support the video tag.
              </video>
            );
          } else if (item.type === "youtube") {
            const youtubeEmbedUrl = `https://www.youtube.com/embed/${item.src}`;
            return (
              <iframe
                key={index}
                width="100%"
                height="100%"
                src={youtubeEmbedUrl}
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
                title="YouTube video"
              ></iframe>
            );
          } else {
            return null;
          }
        })}
      </div>
    </AnimatePresence>
  );
}

export default MainPage;
