import "./mainPage.css";
import { AnimatePresence, motion } from "framer-motion";
import gsap from "gsap";
import { useEffect, useRef } from "react";

const media = [
  {
    type: "video",
    src: "/ocean.mp4",
  },
  {
    type: "",
    src: "",
  },
];

function MainPage() {
  useEffect(() => {
    const screenHeight = window.innerHeight;
    const startX = screenHeight / 2;
    const offset = parseInt("50px", 10);
    const startY = startX - offset;

    gsap.fromTo(
      ".circle",
      { y: startY },
      { delay: 2, y: 0, duration: 2.5, ease: "power1.inOut" }
    );
  }, []);

  useEffect(() => {
    const screenWidth = window.innerWidth;
    const startX = screenWidth / 4;
    const startyX = -screenWidth / 4;

    gsap.fromTo(
      ".textOne",
      { x: startyX },
      { duration: 4.5, x: 0, ease: "slow" }
    );

    gsap.fromTo(
      ".textTwo",
      { x: startX },
      { duration: 4.5, x: 0, ease: "slow" }
    );
  }, []);

  return (
    <AnimatePresence>
      <div className="container">
        <motion.text
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 2, duration: 2 }}
          className="textOne"
        >
          SUNBURNED
        </motion.text>
        <motion.svg
          className="circle"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 5 }}
          width="100"
          height="50"
          viewBox="0 0 512 422"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path fillRule="evenodd" clipRule="evenodd" fill="black" />
        </motion.svg>
        <motion.text
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 2, duration: 2 }}
          className="textTwo"
        >
          MEMOIRS
        </motion.text>
        <div className="imageContainer">
          {media.map((item, index) => {
            if (item.type === "image") {
              return (
                <img
                  key={index}
                  alt="media"
                  src={item.src}
                  width="100%"
                  height="100%"
                />
              );
            } else if (item.type === "video") {
              return (
                <video
                  key={index}
                  width="100%"
                  height="100%"
                  autoPlay={true}
                  loop={true}
                  muted
                >
                  <source src={item.src} type="video/mp4" />
                  Your browser does not support the video tag.
                </video>
              );
            } else if (item.type === "youtube") {
              const youtubeEmbedUrl = `https://www.youtube.com/embed/${item.src}`;
              return (
                <iframe
                  key={index}
                  width="100%"
                  height="100%"
                  src={youtubeEmbedUrl}
                  frameBorder="0"
                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                  allowFullScreen
                  title="YouTube video"
                ></iframe>
              );
            } else {
              return null;
            }
          })}
        </div>
      </div>
    </AnimatePresence>
  );
}

export default MainPage;
